{"cells":[{"metadata":{"_uuid":"3ef7f654-45c7-4201-a015-b097b5430be4","_cell_guid":"ed6012ef-e27d-48af-b4ad-4db4be9aad32","trusted":true},"cell_type":"code","source":"import pandas as pd\nimport numpy as np\n\ndef dist(x1,x2):\n    return np.sqrt(sum((x1-x2)**2)) # calculating distance\n\n# main algo \ndef knn(X,Y,queryPoint,k=5):\n    \n    vals = [] # creating list to append all distances\n    m = X.shape[0]\n    \n    for i in range(m):\n        d = dist(queryPoint,X[i])\n        vals.append((d,Y[i])) #appending all distances \n        \n    #sorting the list\n    vals = sorted(vals)\n    # choose first k distances \n    vals = vals[:k]\n    \n    vals = np.array(vals)\n\n    \n    new_vals = np.unique(vals[:,1],return_counts=True)\n    \n    index = new_vals[1].argmax()\n    pred = new_vals[0][index]\n    \n    return pred\n","execution_count":null,"outputs":[]}],"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"}},"nbformat":4,"nbformat_minor":4}